    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;


DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20250119142713_Initial') THEN
    CREATE TABLE "ForecastUnit" (
        "Id" integer GENERATED BY DEFAULT AS IDENTITY,
        "Date" text NOT NULL,
        "Temperature" double precision NOT NULL,
        "About" text NOT NULL,
        "Region" text NOT NULL,
        "Response" text NOT NULL,
